CREATE TABLE IF NOT EXISTS "food" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"name" VARCHAR(255) NOT NULL,
	"brand" VARCHAR(255) NOT NULL,
	"product" VARCHAR(255) NOT NULL,
	"calories" DECIMAL NOT NULL,
	PRIMARY KEY("id")
);




CREATE TABLE IF NOT EXISTS "tag" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"tag" VARCHAR(255) NOT NULL,
	PRIMARY KEY("id")
);




CREATE TABLE IF NOT EXISTS "food_recipe" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"food_id" INTEGER NOT NULL,
	"recipe_id" INTEGER NOT NULL,
	"quantity" NUMERIC NOT NULL,
	"descriptor" VARCHAR(255) NOT NULL,
	PRIMARY KEY("id")
);




CREATE TABLE IF NOT EXISTS "recipe" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"title" VARCHAR(255) NOT NULL,
	"time" INTEGER NOT NULL,
	"preview" VARCHAR(255) NOT NULL,
	"ratings" INTEGER NOT NULL,
	"instructions" VARCHAR(255) NOT NULL,
	PRIMARY KEY("id")
);




CREATE TABLE IF NOT EXISTS "recipe_tag" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"tag_id" INTEGER NOT NULL,
	"recipe_id" INTEGER NOT NULL,
	PRIMARY KEY("id")
);




CREATE TABLE IF NOT EXISTS "image" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"recipe_id" INTEGER NOT NULL,
	"image" BYTEA NOT NULL,
	"thumbnail" BYTEA NOT NULL,
	PRIMARY KEY("id")
);



ALTER TABLE "recipe_tag"
ADD FOREIGN KEY("tag_id") REFERENCES "tag"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "recipe_tag"
ADD FOREIGN KEY("recipe_id") REFERENCES "recipe"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "food_recipe"
ADD FOREIGN KEY("recipe_id") REFERENCES "recipe"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "food_recipe"
ADD FOREIGN KEY("food_id") REFERENCES "food"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "image"
ADD FOREIGN KEY("recipe_id") REFERENCES "recipe"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;